#============================================================================================#

# MOSTRAR SOLO UNA IMAGEN

# $a0 direccion del array
# $a1 direcion de inicio del display
MOSTRAR_UNO:
move $t6, $a0		# t6 direccion del array
move $t7, $a1		# t7 direccion del display
li $t5,0		# columna actual

WHILE_COLUMNA_UNO:                     # mientras mostremos una columna
lw $t1, 0($t6)                     # cargamos la palabra de la direccion dem meoria del array
beq $t1, 0x01000000, SALTAR_FILA_UNO            # si nos encontramos con un cero hay que saltar a la siguiente fila
sw $t1, 0($t7)                     # si no nos saltamos la fila, entonces copiamos el valor al display
addi $t6, $t6, 4                   # nos movemos una palabra en el array
addi $t7, $t7, 4                   # nos movemos una palabra en el display
j WHILE_COLUMNA_UNO

SALTAR_FILA_UNO:                        # Nos vamos a saltar una fila, pero debemos revisar si no hemos terminado (dos ceros seguidos)
addi $t6, $t6, 4                    # Nos movemos al siguiente elemento del array
lw $t1, 0($t6)                      # cargamos el siguiente elemento del array
beq $t1, 0x01000000, FIN_MOSTRAR_UNO             # ¿Hay un cero?, entonces encontramos dos ceros seguidos y terminamos

addi $t5, $t5, 1                    # ¿no encontramos un cero?, entonces nos movemos a la siguiente columa

mul $t1, $t5, 1024                  # la direccion de la siguiente columna es: NUMERO_COLUMNA * 1024),
				    # 1024 ya que es un array de words: 4*256

add $t7, $a1, $t1                   # la nueva direccion del display es: NUMERO_COLUMNA * 2048 + Display => offset + display
j WHILE_COLUMNA_UNO                   # mostramos la siguiente columa

FIN_MOSTRAR_UNO:                        # Encontramos los dos ceros seguidos asi que terminamos
jr $ra                              # retornamos

#===========================================================================================#

# MOSTRAR UNA FILA DE IMAGENES

# $a0 direccion del array de la imagen
# $a1 direcion de inicio del display
# $a2 tamanio del buscaminas (ejemplo: 16)

MOSTRAR_FILA:
move $t6, $a0		# t6 direccion del array de la imagen
move $t7, $a1		# t7 direccion del inicio del display

addi $sp,$sp,-8		# Se asigna memoria al stack pointer para guardar el contador de columnas(0) y el inicio del display (4)
sw $a1,4($sp)		# se guarda el inicio del display
li $t5, 0	# columna actual   
sw $t5,0($sp)		# se guarda la inicializacion de t5


WHILE_MOSTRAR_FILA:  	# mientras mostremos una columna
addi $sp,$sp,-4		# Se asigna memoria al stack pointer para guardar el final de la funcion
sw $ra,0($sp)		# Se guarda el salto para no reemplazarlo
jal MOSTRAR_UNO		# Se dibuja la imagen
lw $ra,0($sp)
addi $sp,$sp,4
addi $a1,$a1,64		# Se pasa a la siguiente columna 16 bits mas delante 16x4
lw $t5,0($sp)		# Se carga la posicion de la columna
addi $t5,$t5,1		# Si no se suma uno al contador
beq $t5, $a2, FIN_MOSTRAR_FILA   # Se valida si el contador es igual al tamaño de array
sw $t5,0($sp)		# Se guarda el contador
j WHILE_MOSTRAR_FILA

FIN_MOSTRAR_FILA:                       # Se encuentra que el contador es igual al tamaño del busca minas
lw $a1,4($sp)
addi $sp,$sp,8
jr $ra                              	# retornamos

#=======================================================================================================#


# MOSTRAR UNA COLUMNA DE IMAGENES

# $a0 direccion del array de la imagen
# $a1 direcion de inicio del display
# $a2 tamanio del buscaminas (ejemplo: 16)

MOSTRAR_COLUMNA:
move $t6, $a0		# t6 direccion del array de la imagen
move $t7, $a1		# t7 direccion del inicio del display

addi $sp,$sp,-8		# Se asigna memoria al stack pointer para guardar el contador de columnas(0) y el inicio del display (4)
sw $a1,4($sp)		# se guarda el inicio del display
li $t5, 0		# fila actual   
sw $t5,0($sp)		# se guarda la inicializacion de t5


WHILE_MOSTRAR_COLUMNA:

lw $a1,4($sp)
addi $sp,$sp,-4		# Se asigna memoria al stack pointer para guardar el final de la funcion
mul $t1,$t5,16384	# Se calcula la prixima columna de escritura que es multiplo de 16384. 1024x16
sw $ra,0($sp)		# Se guarda el salto para no reemplazarlo
add $a1,$a1,$t1
sw $t5,4($sp)
jal MOSTRAR_FILA
lw $ra,0($sp)
addi $sp,$sp,4
lw $t5,0($sp)		# Se carga la fila actual
addi $t5,$t5,1
beq $t5, $a2, FIN_MOSTRAR_COLUMNA   # Se valida si el contador es igual al tamaño de array
j WHILE_MOSTRAR_COLUMNA

FIN_MOSTRAR_COLUMNA:                       # Se encuentra que el contador es igual al tamaño del busca minas
lw $a1,4($sp)
addi $sp,$sp,8
jr $ra    

#======================================================================#

# GENERAR TABLERO ALEATORIO

# CARGA EL TABLERO Y LO INICIALIZA, Y MIENTRAS HACE ESTO AGREGA BOMBAS ALEATORIAMENTE

# $a0 DIMENSION DEL TABLERO
# $a1 CANTIDAD DE BOMBAS

CARGAR_TABLERO:
	li $t0,0	# CONTADOR DE POSICION X
	li $t1,0	# CONTADOR DE POSICION Y
	addi $t5,$a0,1
	la $t3,tablero	# SE CARGA EL TABLERO
	
	move $s0,$a0
	move $s1,$a1
	
	WHILE_CARGAR_TABLERO: # MIENTRAS EL CONTADOR NO SEA IGUAL AL TAMAÑO DEL TABLERO
		beq $t0,0,SUMA_WHILE_CARGAR_TABLERO	# POSICION X SI ES 0 O N+1, LAS POSICIONES X E Y
		beq $t0,$t5,SUMA_WHILE_CARGAR_TABLERO	# SE CAMBIAN A -1 POSICIONES NO VALIDAS PARA MODIFICAR
		beq $t1,0,SUMA_WHILE_CARGAR_TABLERO	# SE HACE EN N+1 PORQUE EL TABLERO REALMENTE POSEE DOS DIMENSIONES MAS
		beq $t1,$t5,SUMA_WHILE_CARGAR_TABLERO	# POR EJEMPLO UN TABLERO DE 16X16 TENDRA 18X18 "LISTAS" DEL 0 A LA 17 (N+1)
		sw $t0,0($t3)	# SE GUARDA LA POSICION X EN EL TABLERO
		sw $t1,4($t3)	# SE GUARDA LA POSICION Y EN EL TABLERO
		j SUMA_WHILE_CARGAR_TABLERO_AUX
		SUMA_WHILE_CARGAR_TABLERO:
			li $t4,-1		
			sw $t4,0($t3)	# SE GUARDA -1 EN LA POSICION X DEL TABLERO 
			li $t4,-1	
			sw $t4,4($t3)	# SE GUARDA -1 EN LA POSICION Y DEL TABLERO 
		SUMA_WHILE_CARGAR_TABLERO_AUX:
		addi $t1,$t1,1	# SE SUMA UNO A LA POSICION Y
		#sw $zero,8($t3) # siempre parte en 0 cuando se crea la memoria?
		addi $t3,$t3,12	# SE SUMA 12 PARA PASAR A LA PROXIMA POSICION EN MEMORIA CORRESPONDIENTE A LA SIGUIENTE POSICION X
		addi $t4,$t5,1	# SE SUMA UNO PARA LA VALIDACION DE N+2 	
		bne $t1,$t4,WHILE_CARGAR_TABLERO
	addi $t0,$t0,1	# SE SUMA UNO A LA POSICION X
	li $t1,0	# SE REINICIA LA POSICION Y
	addi $t4,$t5,1	# SE SUMA UNO PARA LA VALIDACION DE N+2
	bne $t0,$t4,WHILE_CARGAR_TABLERO
	
	
	# AGREGAR UNA BOMBA
	# LAS BOMBAS ESTAN REPRESENTADAS POR LA CANTIDAD DE BOMBAS 9
	
	
	# SE INICIAN EN 1,1 PORQUE LAS DEMAS POSICIONES SE UTILIZAN PARA QUE NO SE CAIGA EL PROGRAMA O NO SE HAGA
	# UNA SUMA DONDE NO CORRESPONDA AL HACER LA ASIGNACION DE BOMBAS
	
	addi $sp,$sp,-4
	sw $ra,0($sp)
	addi $s0,$s0,1
	li $t7,0
	# EN LA FUNCION RANDOM NO SE UTILIZA T0 TENER CUIDADO DE NO PERDER T0 QUE CONTIENE LA DIMENSION DEL TABLERO
	WHILE_CAMBIAR_POSICION_BOMBA:
		beq $t7,$s1,FIN_CARGAR_TABLERO
		
		# SE CALCULA UN RANDOM PARA POSICION X
		li $a0,1
		move $a1,$s0
		jal RANDOM_3	# LLAMAR FUNCION RANDOM LIMITE INFERIOR 1 Y LIMITE SUPERIOR (NUMERO RANDOM ENTRE 1 Y LIMITE SUPERIOR)
		addi $sp,$sp,-4
		sw $v0,0($sp)	# SE GUARDA LA POSICION X DEL RANDOM POSICION REAL DE $RA ES 4
		
		# SE CALCULA UN RANDOM PARA POSICION Y
		li $a0,1
		move $a1,$s0
		jal RANDOM_3	# LLAMAR FUNCION RANDOM LIMITE INFERIOR 1 Y LIMITE SUPERIOR (NUMERO RANDOM ENTRE 1 Y LIMITE SUPERIOR)
		addi $sp,$sp,-4
		sw $v0,0($sp)	# SE GUARDA LA POSICION Y DEL RNDOM POSICION REAL DE POSICION X ES 4 $RA ES 8
		# YA NO IMPORTA T0
		
		lw $a0,4($sp)	# POSICION X
		lw $a1,0($sp)	# POSICION Y
		addi $sp,$sp,8
		li $a2,9	# NUMERO REPRESENTATIVO PARA LA BOMBA
		jal CAMBIAR_POSICION_BOMBA
		beq $v0,1,CAMBIO_BOMBA_EXITOSO
		
		j CAMBIO_BOMBA_EXITOSO_AUX
			CAMBIO_BOMBA_EXITOSO:
			addi $t7,$t7,1
		CAMBIO_BOMBA_EXITOSO_AUX:
	j WHILE_CAMBIAR_POSICION_BOMBA
FIN_CARGAR_TABLERO:
lw $ra,0($sp)
addi $sp,$sp,4
jr $ra

#=======================================================================================#

# SE CAMBIA EN LAS POSICIONES DADAS POR EL NUMERO DADO
# SE CARGA EL TABLERO SE ENCUENTRA LA POSICION Y SE CAMBIA SU VALOR
# RETORNA EN V0 0 SI NO SE AGREGA BOMBA, 1 SI FUE AGREGADA LA BOMBA

# $A0 POSICION X
# $A1 POSICION Y
# $A2 NUMERO DE BOMBAS
# $v0 RETORNO 0 Ó 1

CAMBIAR_POSICION_BOMBA:
	la $t3,tablero	# SE CARGA EL TABLERO
#	addi $t3,$t3,12	# SE SUMA 12 PARA AVANAZAR A LA POSICION Y = 1
	mul $t0,$a0,216	# SE CALCULAN EL MOVIMIENTO EN EL EJE X
	add $t3,$t3,$t0	# SE APLICA EL MOVIMIENTO
	mul $t1,$a1,12	# SE CALCULAN EL MOVIMIENTO EN EL EJE Y
	add $t3,$t3,$t1	# SE APLICA EL MOVIMIENTO
#	addi $t3,$t3,216# SE SUMA LA PRIMERA FILA 
	lw $t0,8($t3)
	bne $t0,9,AGREGA_BOMBA
	j AGREGA_BOMBA_AUX
		AGREGA_BOMBA:
			sw $a2,8($t3)	# SE GUARDA LA CANTIDAD DE BOMBA EN EL ESPACIO DE CANTIDAD DE BOMBA EN EL TABLERO
			li $v0,1	# SE RETORNA 1
			j FIN_CAMBIAR_POSICION_BOMBA
	AGREGA_BOMBA_AUX:
	li $v0,0	# SE RETORNA 0
FIN_CAMBIAR_POSICION_BOMBA:
jr $ra

#====================================================================================#

# GENERA RANDOM

# $a0 LIMITE INFERIOR
# $a1 LIMITE SUPERIOR
# RETORNO EN $v0
RANDOM_3:
	addi $sp,$sp,-4	# SE ASIGNA MEMORIA PARA GUARDAR EL LIMITE INFFERIOR
	sub $a1,$a1,$a0	# SE CALCULA EL LIMITE SUPERIOR PARA EL CAULCUO, ES DECIR LIMITE SUPERIOR $A1 - LIMITE INFERIOR $A0, 
			# PARA SUMAR EL LIMITE INFERIOR NO DE MAYOR AL LIMITE SUPERIOR REAL
	sw $a0,0($sp)
	li $v0,42	# ID RANDIOM
	syscall		# CALCULA EL RANDOM ENTRE 0 Y EL LIMITE SUPERIOR $a1 CON $a1 EXCLUYENTE
	lw $t1,0($sp)	# SE CARGA EL LIMITE INFERIOR
	addi $sp,$sp,4	# DEVUELVO MEMORIA AL STACK
	add $v0,$a0,$t1 # SUMA EL LIMITE INFERIOR $t1 CON EL RESULTADO $a0 Y RETORNA RESULTADO $v0
FIN_RANDOM_3:
	jr $ra	
	
	
#============================================================================================#

# GENERA ALEATORIAMENTE UN 0 O UN 1

RANDOM_VALIDEZ:
	li $a0,0
	li $a1,2	# LIMITE SUPERIOR 2, SE CALCULARA ALGUN VALOR ENTRE 0 O 1
	li $v0,42	# ID RANDOM
	syscall		# CALCULA EL RANDOM ENTRE 0 Y EL LIMITE SUPERIOR $a1 CON $a1 EXCLUYENTE
	move $v0,$a0
FIN_RANDOM_VALIDEZ:
	jr $ra

FIN_FUNCIONES:
